import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import styled from "styled-components";
import "@fortawesome/fontawesome-free/css/all.min.css";

const SearchBar = styled.input`
  width: 80%;
  padding: 10px 20px;
  box-sizing: border-box;
  border: solid var(--white) 1px;
  background: none;
  color: var(--white);
  outline: none;
  z-index: 5;
  &::placeholder {
    color: var(--white);
  }
`;
const Button = styled.button`
  width: 50px;
`;
const Div = styled.div`
  display: flex;
  gap: 20px;
`;

const CsvReader = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [error, setError] = useState(null);
  const [searchCriteria, setSearchCriteria] = useState({
    searchInput: "",
    playerLastName: "",
    playerId: "",
    position: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/database/player_database.csv");
        const reader = response.body.getReader();
        const result = await reader.read();
        const decoder = new TextDecoder("utf-8");
        const csv = decoder.decode(result.value);
        Papa.parse(csv, {
          header: true,
          complete: (results) => {
            const filteredResults = results.data.map((row) => {
              delete row[""];
              return row;
            });
            setData(results.data);
            setFilteredData(results.data); // Initialize filtered data
            setError(null);
          },
          error: (err) => {
            console.error("Error parsing CSV:", err);
            setError(err);
          },
        });
      } catch (err) {
        console.error("Error fetching CSV:", err);
        setError(err);
      }
    };

    fetchData();
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria({ ...searchCriteria, [name]: value });
  };

  const handleSearch = (searchQuery) => {
    if (!searchQuery) {
      // If no input is provided, return the original data
      setFilteredData(data);
      return;
    }
    const filtered = data.filter((row) => {
      return (row.first_name === searchQuery)(row.last_name === searchQuery)(
        row.player_id === searchQuery
      )(row.level === searchQuery)(
        row.primary_position === searchQuery ||
          (Array.isArray(row.secondary_position) &&
            row.secondary_position.includes(searchQuery))
      );
    });
    setFilteredData(filtered);
  };

  return (
    <div>
      <Div>
        <SearchBar
          placeholder="Search player name, id, or position and press enter"
          value={searchCriteria.searchInput}
          onChange={handleInputChange}
        ></SearchBar>
        <Button onClick={() => handleSearch(searchCriteria.searchInput)}>
          <i class="fa fa-search"></i>
        </Button>
      </Div>

      {error && <div>Error: {error.message}</div>}
      {filteredData.length > 0 && (
        <div>
          <h2>Search Results</h2>
          <pre>{JSON.stringify(filteredData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default CsvReader;

