import styled from "styled-components";
import Nav from "../components/Nav";
import SimButton from "../components/SimButton";
import EditButton from "../components/EditButton";
import PlayerButton from "../components/PlayerButton";

const RightBG = styled.div`
  position: fixed;
  top: 0;
  right: 0;
  width: 50vw;
  height: 100vh;
  background-color: var(--dark-gray);
  z-index: -1;
  display: flex;
  align-items: top;
  justify-content: center;
`;

const BGLine1 = styled.div`
  width: calc(100% - 100px);
  height: calc(100% - 140px);
  margin-top: 100px;
  border: solid 2px var(--gray);
  background-color: transparent;

  z-index: -1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LeftSection = styled.div`
  width: 50vw;
  height: 80vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const BGLine2 = styled(BGLine1)`
  margin: 0;
  width: calc(100% - 20px);
  height: calc(100% - 20px);
`;

const PlayerSection = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -45%);

  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
  justify-content: center;
`;

function Simulator() {
  return (
    <div>
      <Nav />

      <LeftSection>
        <SimButton />
      </LeftSection>

      <PlayerSection>
        <EditButton />
        <PlayerButton name="Player 1" position="C"></PlayerButton>
        <PlayerButton name="Player 2" position="1B"></PlayerButton>
        <PlayerButton name="Player 3" position="2B"></PlayerButton>
        <PlayerButton name="Player 4" position="3B"></PlayerButton>
        <PlayerButton name="Player 5" position="SS"></PlayerButton>
        <PlayerButton name="Player 6" position="LC"></PlayerButton>
        <PlayerButton name="Player 7" position="CF"></PlayerButton>
        <PlayerButton name="Player 8" position="RF"></PlayerButton>
        <PlayerButton name="Player 9" position="??"></PlayerButton>
      </PlayerSection>

      <RightBG>
        <BGLine1>
          {" "}
          <BGLine2></BGLine2>
        </BGLine1>
      </RightBG>
    </div>
  );
}

export default Simulator;
