import styled from "styled-components";
import { useEffect, useState } from "react";

import Nav from "../components/Nav";
import ReportSection from "./sections/ReportSection";
import SimButton from "../components/SimButton";
import fullField from "../images/fullField.png";
import EditButton from "../components/EditButton";
import PlayerButton from "../components/PlayerButton";

const Div = styled.div`
  position: relative;
  height: 100%;
  min-height: 100vh;

  &::before {
    content: "";
    background-color: ${(props) =>
      props.$edit === true ? "var(--blue)" : "var(--dark-gray)"}
    height: 100%;
    width: 50%;
    position: absolute;
    right: 0;
    top: 0;
    z-index: -2;
  }
`;

const Content = styled.div`
  display: flex;
  position: relative;
`;

const BgLine = styled.div`
  border: solid 1px var(--gray);
  height: 650px;
  width: calc(100% - 60px);
  margin: 0px 30px;

  z-index: -1;
  position: relative;
  right: 0;
  overflow: hidden;

  &::before {
    content: "";
    border: solid 1px var(--gray);
    position: absolute;
    height: calc(100% - 20px);
    width: calc(100% - 20px);
    margin: 10px;
    z-index: -1;
  }
`;

const TeamSection = styled.div`
  position: absolute;
  right: 50%;
  transform: translate(50%, 20px);
  z-index: 5;

  display: flex;
  flex-direction: column;
  gap: 10px;
  padding-top: 20px;
`;

const Right = styled.div`
  width: 50%;
`;

const Left = styled(Right)`
  display: flex;
  align-items: center;
  padding-right: 8vw;
  box-sizing: border-box;
  flex-direction: column;
  justify-content: center;
`;

const Img = styled.img`
  width: 500px;
  width: 80%;
  margin: 50px;
`;

function Simulator() {
  const [editMode, setEditMode] = useState(false);

  const handleEditMode = () => {
    setEditMode(!editMode);
  };

  useEffect(() => {
    console.log(editMode);
  }, [editMode]);

  return (
    <Div $edit={editMode.toString}>
      <Nav></Nav>

      <TeamSection>
        <EditButton onClick={handleEditMode} />
        <PlayerButton name="Player 1" position="C" />
        <PlayerButton name="Player 2" position="1B" />
        <PlayerButton name="Player 3" position="2B" />
        <PlayerButton name="Player 4" position="3B" />
        <PlayerButton name="Player 5" position="SS" />
        <PlayerButton name="Player 6" position="LF" />
        <PlayerButton name="Player 7" position="RF" />
        <PlayerButton name="Player 8" position="CF" />
        <PlayerButton name="Player 9" position="??" />{" "}
      </TeamSection>

      <Content>
        <Left>
          <Img src={fullField}></Img>
          <SimButton></SimButton>
        </Left>

        <Right>
          <BgLine></BgLine>
        </Right>
      </Content>
    </Div>
  );
}

export default Simulator;
